import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

const About = () => {
  const chartRef = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');
    new Chart(ctx, {
      type: 'bar',
      data: {
        labels: ['Gold', 'Platinum', 'Diamond', 'Master'],
        datasets: [{
          label: 'Players by Rank',
          data: [120, 95, 60, 20],
          backgroundColor: ['#60a5fa', '#a78bfa', '#f472b6', '#facc15']
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: { display: false }
        }
      }
    });
  }, []);

  return (
    <div className="p-6 max-w-3xl mx-auto text-center">
      <h1 className="text-3xl font-bold mb-4">About This Project</h1>
      <p className="mb-6">This assistant helps Call of Duty players find teammates based on skill, rank, and preferences.</p>
      <canvas ref={chartRef} className="mx-auto" width="400" height="200"></canvas>
    </div>
  );
};

export default About;
